    private void bupdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bupdateActionPerformed
        // TODO add your handling code here:
        if (txtid.equals("")|txtharga.equals("")|txtnama.equals("")
           |txtsatuan.equals("")|txtstok.equals("")) {
           JOptionPane.showMessageDialog(rootPane, "Mohon Lengkapi Data ..!");
        }else{
            int confirmSimpan = JOptionPane.showConfirmDialog(rootPane,"Apakah anda yakin ingin Mengubah data dan data tersebut sudah benar?,"
                                                                        ,"Ubah data",JOptionPane.YES_NO_OPTION );
            if (confirmSimpan == JOptionPane.YES_OPTION){
        controller.update();
        controller.isiTable();
      txtharga.setText("");
        txtnama.setText("");
        txtid.setText("");
        txtsatuan.setText("");
        txtstok.setText("");
        controller.tampilurutan();
            }
        }
    }//GEN-LAST:event_bupdateActionPerformed

