    private void bsimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bsimpanActionPerformed
        // TODO add your handling code here:
        if (txtid.equals("")|txtharga.equals("")|txtnama.equals("")
           |txtsatuan.equals("")|txtstok.equals("")) {
           JOptionPane.showMessageDialog(rootPane, "Mohon Lengkapi Data ..!");
        }else{
            int confirmSimpan = JOptionPane.showConfirmDialog(rootPane,"Apakah anda yakin ingin Menyimpan data dan data tersebut sudah benar?,"
                                                                        ,"Simpan data",JOptionPane.YES_NO_OPTION );
            if (confirmSimpan == JOptionPane.YES_OPTION){
         controller.insert();
         txtharga.setText("");
        txtnama.setText("");
        txtid.setText("");
        txtsatuan.setText("");
        txtstok.setText("");
        controller.isiTable();
        
        controller.tampilurutan();
            }
        }
    }//GEN-LAST:event_bsimpanActionPerformed

